DELIMITER $$
CREATE PROCEDURE sp_cliente_vencido()
    CONTAINS SQL
BEGIN

    UPDATE  cliente, cliente_log
    SET     cliente_log.fecha_fin = now(),
            cliente_log.mod_user = 1,
            cliente_log.mod_fecha = now()
    WHERE   cliente.cliente = cliente_log.cliente
    AND     DATE_FORMAT( cliente.fecha_fin_contrato, '%Y-%m-%d' ) = DATE_FORMAT( DATE_SUB( NOW( ) , INTERVAL 1 DAY ) , '%Y-%m-%d' )
    AND     cliente_log.fecha_fin IS NULL;

    INSERT INTO cliente_log(cliente,estado_anterior,estado,fecha_inicio,fecha_fin,comentario,add_user,add_fecha)
    SELECT  cliente, estado, 'VENCIDO', NOW(), NULL, NULL, 1, NOW()
    FROM    cliente
    WHERE   DATE_FORMAT( fecha_fin_contrato, '%Y-%m-%d' ) = DATE_FORMAT( DATE_SUB( NOW( ) , INTERVAL 1 DAY ) , '%Y-%m-%d' );

    UPDATE cliente
    SET estado = 'VENCIDO'
    WHERE DATE_FORMAT( fecha_fin_contrato, '%Y-%m-%d' ) = DATE_FORMAT( DATE_SUB( NOW( ) , INTERVAL 1 DAY ) , '%Y-%m-%d' );

END
$$
DELIMITER ;

DELIMITER $$
CREATE EVENT e_cliente_vencido
ON SCHEDULE EVERY 1 DAY STARTS '2016-06-07 00:00:00'
COMMENT 'REALIZA EL CAMBIO DE ESTADO DE LOS CLIENTES A VENCIDO CUANDO LA FECHA FIN DEL CONTRATO YA HA PASADO'
DO
    CALL sp_cliente_vencido;
$$
DELIMITER ;

DELIMITER $$
CREATE PROCEDURE sp_cliente_por_vencer()
    CONTAINS SQL
BEGIN

    UPDATE  cliente, cliente_log
    SET     cliente_log.fecha_fin = now(),
            cliente_log.mod_user = 1,
            cliente_log.mod_fecha = now()
    WHERE   cliente.cliente = cliente_log.cliente
    AND     DATE_FORMAT( NOW( ), '%Y-%m-%d' ) = DATE_FORMAT( DATE_SUB( cliente.fecha_fin_contrato , INTERVAL (SELECT intervalo FROM cliente_estado_tiempo WHERE estado = 'POR_VENCER') MONTH ) , '%Y-%m-%d' )
    AND     cliente_log.fecha_fin IS NULL;

    INSERT INTO cliente_log(cliente,estado_anterior,estado,fecha_inicio,fecha_fin,comentario,add_user,add_fecha)
    SELECT  cliente, estado, 'POR_VENCER', NOW(), NULL, NULL, 1, NOW()
    FROM    cliente
    WHERE   DATE_FORMAT( NOW( ), '%Y-%m-%d' ) = DATE_FORMAT( DATE_SUB( fecha_fin_contrato , INTERVAL (SELECT intervalo FROM cliente_estado_tiempo WHERE estado = 'POR_VENCER') MONTH ) , '%Y-%m-%d' );

    UPDATE cliente
    SET estado = 'POR_VENCER'
    WHERE   DATE_FORMAT( NOW( ), '%Y-%m-%d' ) = DATE_FORMAT( DATE_SUB( fecha_fin_contrato , INTERVAL (SELECT intervalo FROM cliente_estado_tiempo WHERE estado = 'POR_VENCER') MONTH ) , '%Y-%m-%d' );

END
$$
DELIMITER ;

