CREATE EVENT e_cliente_por_vencer
ON SCHEDULE EVERY 1 DAY STARTS '2016-06-07 00:00:00'
COMMENT 'REALIZA EL CAMBIO DE ESTADO DE LOS CLIENTES POR VENCER'
DO
    CALL sp_cliente_por_vencer;
$$
